<?php if (!defined('BASEPATH')) exit('No direct script access allowed');/** * masort: http://de3.php.net/manual/de/function.uasort.php#25882 *  * Sorts array((array(),array()) by $sortby **/function masort(&$data, $sortby){	if(is_array($sortby))	{		$sortby = join(',',$sortby);	}	uasort($data,create_function('$a,$b','		$skeys = split(\',\',\''.$sortby.'\');		foreach($skeys as $key)		{			if (is_numeric($a[$key]) && is_numeric($b[$key]))			{				if ($a[$key] == $b[$key]) 				{					return 0;				}				return ($a[$key] < $b[$key]) ? 1 : -1;			}			else if( ($c = strcasecmp($a[$key],$b[$key])) != 0 )			{				return($c);			}		}		return($c); '));}function dotlan_url($url, $module = 'map'){	$search = array(' ');	$replace = array('_');		return ("http://evemaps.dotlan.net/{$module}/".str_replace($search, $replace, $url));}?>